import React, { useState, useEffect } from 'react';
import * as monaco from 'monaco-editor';

// Компонент для редактора VHDL
const VHDLEditor = ({ id, onCodeChange, initialCode }) => {
  useEffect(() => {
    const editorInstance = monaco.editor.create(document.getElementById(id), {
      value: initialCode,
      language: 'vhdl',
      theme: 'vs-dark',
      automaticLayout: true,
    });

    // Отслеживаем изменения в редакторе
    const debounceChangeHandler = debounce(() => {
      const code = editorInstance.getValue();
      onCodeChange(code); // Передаем код в родительский компонент
    }, 300); // Задержка в 300ms

    editorInstance.onDidChangeModelContent(debounceChangeHandler);

    return () => {
      editorInstance.dispose(); // Очистка редактора при удалении компонента
    };
  }, [id, initialCode, onCodeChange]);

  return <div id={id} style={{ height: '400px', width: '100%' }}></div>;
};

// Функция debounce, чтобы уменьшить частоту вызовов
function debounce(func, wait) {
  let timeout;
  return function () {
    const context = this;
    const args = arguments;
    clearTimeout(timeout);
    timeout = setTimeout(() => func.apply(context, args), wait);
  };
}

// Компонент для консоли вывода
const CompilerConsole = ({ output }) => {
  return (
    <div className="compiler-console" style={{ height: '150px', overflowY: 'auto', backgroundColor: '#f8f8f8', border: '1px solid #ccc', padding: '10px' }}>
      <h3>Консоль:</h3>
      <pre>{output}</pre>
    </div>
  );
};

// Основной компонент
const VHDLEditorWithCompiler = () => {
  const [vhdlCode, setVhdlCode] = useState(`-- Напишите вашу архитектуру здесь`);
  const [testbenchCode, setTestbenchCode] = useState(`-- Напишите ваш тестовый стенд здесь`);
  const [output, setOutput] = useState('');

  // Функция для компиляции
  const compileCode = async () => {
    try {
      const response = await fetch('http://localhost:5000/compile-vhdl', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ vhdlCode, testbenchCode }), // Отправляем и архитектуру, и тестовый стенд
      });

      const result = await response.text();
      setOutput(result); // Отображаем вывод компиляции
    } catch (error) {
      setOutput('Ошибка компиляции: ' + error.message);
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: '20px', padding: '20px' }}>
      <div>
        <h3>Архитектура:</h3>
        <VHDLEditor id="architecture-editor" onCodeChange={setVhdlCode} initialCode={vhdlCode} />
      </div>
      <div>
        <h3>Тестовый стенд:</h3>
        <textarea
          id="testbench-textarea"
          value={testbenchCode}
          onChange={(e) => setTestbenchCode(e.target.value)}
          placeholder="Введите код тестового стенда..."
          style={{
            width: '100%',
            height: '150px',
            fontFamily: 'monospace',
            fontSize: '14px',
            border: '1px solid #ccc',
            padding: '10px',
          }}
        />
      </div>
      <button onClick={compileCode} style={{ padding: '10px 20px', fontSize: '16px' }}>
        Компилировать
      </button>
      <CompilerConsole output={output} />
    </div>
  );
};

export default VHDLEditorWithCompiler;
