import React, { useState } from 'react';
import GraphComponent, { FileUploader } from './components/GraphComponent';
export default function App() {
  const [graph, setGraph] = useState(null);

  const handleGraphUpdate = (newGraphData) => {
    setGraph(newGraphData);
  };

  return (
    <>
      {/* Передаем graph и функцию обновления в FileUploader */}
      <FileUploader graph={graph} onGraphUpdate={handleGraphUpdate} />
      {/* Передаем graph в GraphComponent */}
      <GraphComponent graph={graph} />
    </>
  );
}
