import * as monaco from 'monaco-editor';

// Функция для генерации списка ключевых слов в разных регистрах
function generateKeywords(str) {
  const words = str.split(",");
  const keywords = {};
  words.forEach((word) => {
    const lower = word.trim().toLowerCase();
    const upper = word.trim().toUpperCase();
    const capitalized = word.trim().charAt(0).toUpperCase() + word.trim().slice(1).toLowerCase();
    keywords[lower] = true;
    keywords[upper] = true;
    keywords[capitalized] = true;
  });
  return Object.keys(keywords);
}

// Основные ключевые слова VHDL
const keywords = generateKeywords(`
  abs,access,after,alias,all,and,architecture,array,assert,attribute,begin,block,
  body,buffer,bus,case,component,configuration,constant,disconnect,downto,else,elsif,end,
  entity,exit,file,for,function,generate,generic,generic map,if,impure,in,inertial,inout,
  is,label,library,linkage,loop,map,mod,nand,new,next,nor,null,of,on,open,or,others,out,
  package,port,postponed,procedure,process,pure,range,record,register,reject,rem,report,
  return,rol,ror,select,severity,signal,sla,sll,sra,srl,subtype,then,to,transport,type,
  unaffected,units,until,use,variable,wait,when,while,with,xnor,xor
`);

// Регистрация языка VHDL
monaco.languages.register({ id: 'vhdl' });

// Определение подсветки
monaco.languages.setMonarchTokensProvider('vhdl', {
  keywords,
  operators: [
    '<=', '=>', '=', '/=', '<', '<=', '>', '>=', '+', '-', '*', '/', '&', '|', 'and', 'or', 'not'
  ],
  symbols: /[=><!~?:&|+\-*\/\^%]+/,
  brackets: [
    { open: '(', close: ')', token: 'bracket' },
    { open: '[', close: ']', token: 'bracket' },
    { open: '{', close: '}', token: 'bracket' },
  ],
  tokenizer: {
    root: [
      // Ключевые слова
      [new RegExp(`\\b(${keywords.join('|')})\\b`, 'i'), 'keyword'],

      // Числа
      [/\b\d+(\.\d+)?([eE][\-+]?\d+)?\b/, 'number'],

      // Комментарии
      [/--.*/, 'comment'],

      // Операторы
      [/[=><!~?:&|+\-*\/\^%]+/, 'operator'],

      // Строки
      [/"([^"\\]|\\.)*$/, 'string.invalid'], // Неполная строка
      [/"([^"\\]|\\.)*"/, 'string'],

      // Скобки
      [/[{}\[\]()]/, '@brackets'],

      // Идентификаторы
      [/\b[a-zA-Z_][a-zA-Z0-9_]*\b/, 'variable'],
    ],
  },
});

// Настройка темы
monaco.editor.defineTheme('vhdlTheme', {
  base: 'vs-dark',
  inherit: true,
  rules: [
    { token: 'keyword', foreground: 'c586c0', fontStyle: 'bold' }, // Ключевые слова: светло-фиолетовые
    { token: 'number', foreground: 'dcdcaa' }, // Числа: светло-жёлтые
    { token: 'string', foreground: 'ce9178' }, // Строки: оранжевые
    { token: 'comment', foreground: '6a9955', fontStyle: 'italic' }, // Комментарии: зелёные и курсивные
    { token: 'operator', foreground: 'd7ba7d' }, // Операторы: жёлтые
    { token: 'variable', foreground: 'ff0000' }, // Идентификаторы: светло-голубые
    { token: 'brackets', foreground: 'd4d4d4' }, // Скобки: серые
    { token: 'string.invalid', foreground: 'f44747', fontStyle: 'bold' }, // Ошибки в строках: красные
  ],
  colors: {
    'editor.foreground': '#ffffff',
    'editor.background': '#1e1e1e',
    'editorLineNumber.foreground': '#858585',
    'editorCursor.foreground': '#d7ba7d',
    'editor.selectionBackground': '#264f78',
  },
});
