// Функция для чтения XML файла
function readXMLFile(file, callback) {
  const reader = new FileReader();
  reader.onload = function(event) {
    const xmlContent = event.target.result;  // Текст файла
    callback(xmlContent);  // Передаём текст файла в callback
  };
  reader.readAsText(file);
}


const loadGraphFromXml = (xml, graph) => {
  // Преобразуем текст XML в DOM элемент
  const node = mxUtils.parseXml(xml).documentElement;

  // Очищаем текущую модель графа
  graph.getModel().beginUpdate();
  try {
    graph.getModel().clear(); // Очищаем текущий граф
    const codec = new maxCodec();  // Создаем декодер
    codec.decode(node, graph.getModel());  // Декодируем XML и загружаем в модель графа
  } finally {
    graph.getModel().endUpdate();  // Завершаем транзакцию
  }
};
