import React, { useState } from 'react';
import GraphComponent from './components/GraphComponent';
import FileUploader from './components/FileUploader'; // Предполагается, что FileUploader не экспортируется по умолчанию

export default function App() {
  const [graph, setGraph] = useState(null);

  // Функция для обновления графа
  const updateGraph = (newGraph) => {
    setGraph(newGraph);
  }

  return (
    <>
      {/* Передаем функцию обновления графа в FileUploader */}
      <FileUploader updateGraph={updateGraph} />
      {/* Передаем граф в GraphComponent */}
      <GraphComponent graph={graph} />
    </>
  );
}
