import React, { useEffect, useRef } from 'react';
import { renderWaveForm } from 'wavedrom';
import { parse } from 'vcd-parser';

const SignalGraph = () => {
  const containerRef = useRef(null);

  // Пример VCD контента
  const vcdContent = `
    $date today $end
    $timescale 1 ns $end
    $var wire 1 ! signal1 $end
    $var wire 1 # signal2 $end
    $upscope $end
    $enddefinitions $end
    $dumpvars
    0 ! 0
    0 # 1
    $end
    #0
    1 ! 1
    #10
    0 ! 0
    #20
    1 # 0
    #30
    0 # 1
    #40
    $end
  `;

  useEffect(() => {
    if (vcdContent && containerRef.current) {
      try {
        // Парсим содержимое VCD-файла
        const parsedData = parse(vcdContent);
        console.log('Parsed VCD Data:', parsedData); // Log parsed data

        // Преобразуем данные в формат WaveDrom
        const waveData = convertVCDToWaveDrom(parsedData);
        console.log('WaveDrom Data:', waveData); // Log WaveDrom data

        // Рендерим график сигналов с помощью WaveDrom
        renderWaveForm(waveData, containerRef.current);
      } catch (error) {
        console.error('Ошибка обработки VCD-файла:', error);
      }
    }
  }, [vcdContent]);

  // Преобразуем данные VCD в формат WaveDrom
  const convertVCDToWaveDrom = (parsedData) => {
    const signals = Object.keys(parsedData.signals).map((key) => {
      const signal = parsedData.signals[key];
      const wave = signal.transitions.map((transition) => {
        switch (transition.value) {
          case '1':
            return '1';
          case '0':
            return '0';
          case 'x':
            return 'x';
          case 'z':
            return 'z';
          default:
            return '.'; // Пустое состояние
        }
      }).join('');

      return { name: key, wave };
    });

    return { signal: signals };
  };

  return <div ref={containerRef}></div>;
};

export default SignalGraph;
