import * as monaco from 'monaco-editor';
я
// Define VHDL language
monaco.languages.register({ id: 'vhdl' });

// Set up the syntax highlighting rules (Monarch Tokens)
monaco.languages.setMonarchTokensProvider('vhdl', {
  keywords: [
    'library', 'use', 'entity', 'architecture', 'is', 'port', 'in', 'out', 'inout',
    'begin', 'end', 'signal', 'process', 'if', 'then', 'else', 'elsif', 
    'case', 'when', 'loop', 'while', 'for', 'generate', 'component', 
    'generic', 'map', 'report', 'severity', 'not', 'and', 'or', 'nor', 'nand', 
    'xor', 'xnor', 'others'
  ],
  operators: [
    '<=', '=>', '=', '/=', '<', '<=', '>', '>=', '+', '-', '*', '/', '&', '|', 
    'and', 'or', 'not', 'nor', 'nand', 'xor', 'xnor'
  ],
  symbols: /[=><!~?:&|+\-*\/\^%]+/,
  tokenizer: {
    root: [
      // Keywords
      [/\b(?:library|use|entity|architecture|is|port|in|out|inout|begin|end|signal|process|if|then|else|elsif|case|when|loop|while|for|generate|component|generic|map|report|severity|not|and|or|nor|nand|xor|xnor|others)\b/, 'keyword'],
      
      // Logical expressions (such as 'not', 'and', 'or', etc.)
      [/\b(?:not|and|or|nor|nand|xor|xnor)\b/, 'logical-operator'],
      
      // Numbers (integers, decimals, and hex)
      [/\b\d+(\.\d+)?([eE][\-+]?\d+)?\b/, 'number'],
      
      // Comments
      [/--.*/, 'comment'],
      
      // Operators (e.g., assignment and comparison)
      [/[=><!~?:&|+\-*\/\^%]+/, 'operator'],
      
      // Identifiers (e.g., signal names, variable names)
      [/\b[a-zA-Z_][a-zA-Z0-9_]*\b/, 'identifier'],
      
      // Strings
      [/"([^"\\]|\\.)*$/, 'string.invalid'], // Incomplete string
      [/"([^"\\]|\\.)*"/, 'string'],

      // Handle 'if-then-else', 'case' expressions, etc.
      [/\bif\b/, 'keyword'],
      [/\belsif\b/, 'keyword'],
      [/\belse\b/, 'keyword'],
      [/\bcase\b/, 'keyword'],
      [/\bwhen\b/, 'keyword'],
      [/\bothers\b/, 'keyword'],
      
      // Handle logical grouping (such as parentheses in expressions)
      [/\(/, 'parenthesis'],
      [/\)/, 'parenthesis'],
      
      // Process block detection
      [/\bprocess\b/, 'keyword'],
      [/\bbegin\b/, 'keyword'],
      [/\bend\b/, 'keyword'],
      
      // Component instantiation
      [/\bcomponent\b/, 'keyword'],
      [/\bport map\b/, 'keyword'],
      [/\bgeneric map\b/, 'keyword'],
    ],
    
    // Rules for different blocks (handling keywords inside logical expressions)
    logicalExpressions: [
      // Handle 'not' with logical expressions
      [/\bnot\b\s+[a-zA-Z_][a-zA-Z0-9_]*/, 'logical-operator'],
      
      // Handle logical conjunctions like 'and', 'or', etc.
      [/\b(?:and|or|nor|nand|xor|xnor)\b/, 'logical-operator'],
      
      // Handle conditionals and expressions
      [/\b(?:if|elsif|else|then)\b/, 'keyword'],
      
      // Handle case statements
      [/\b(?:case|when|others)\b/, 'keyword'],
      
      // Logical expressions like: (not signal_1) and (signal_2 xor signal_3)
      [/\(.*\)/, 'expression'],
    ],
  },
});

monaco.editor.defineTheme('my-custom-dark', {
  base: 'vs-dark', // Базовая тема (переопределим её)
  inherit: true, // Наследование от стандартной темы
  rules: [
    { token: 'comment', foreground: 'b0b0b0', fontStyle: 'italic' }, // Комментарии в сером цвете
    { token: 'keyword', foreground: 'ff79c6', fontStyle: 'bold' }, // Ключевые слова — розовый, жирный шрифт
    { token: 'identifier', foreground: 'f8f8f2' }, // Идентификаторы — светлый цвет
    { token: 'number', foreground: 'bd93f9' }, // Числа — светлый фиолетовый
    { token: 'string', foreground: '50fa7b' }, // Строки — светлый зелёный
    { token: 'operator', foreground: 'ff5555' }, // Операторы — красный
    { token: 'type', foreground: 'ffb86c' }, // Типы данных — оранжевый
    { token: 'function', foreground: 'f1fa8c' }, // Функции — жёлтый
    { token: 'variable', foreground: '8be9fd' }, // Переменные — голубой
    { token: 'parenthesis', foreground: 'ffb86c' }, // Скобки — оранжевый
    { token: 'constant', foreground: 'ff5555' }, // Константы — красный
    { token: 'keyword.operator', foreground: 'ff79c6' }, // Операторы ключевых слов — розовый
    { token: 'string.quote', foreground: '8be9fd' }, // Кавычки для строк — голубой
    { token: 'number.hex', foreground: 'bd93f9' }, // Шестнадцатеричные числа — фиолетовый
    { token: 'predefined', foreground: 'f1fa8c' }, // Предопределённые имена — жёлтый
    { token: 'type.identifier', foreground: 'f8f8f2' }, // Идентификаторы типов — светлый
  ],
  colors: {
    'editor.foreground': '#f8f8f2', // Цвет текста
    'editor.background': '#101015', // Цвет фона
    'editorCursor.foreground': '#f8f8f0', // Цвет курсора
    'editor.lineHighlightBackground': '#44475a', // Цвет подсветки линии
    'editorLineNumber.foreground': '#6272a4', // Цвет номеров строк
    'editor.selectionBackground': '#44475a', // Цвет фона выделения
    'editor.inactiveSelectionBackground': '#44475a', // Цвет фона неактивного выделения
    'editor.selectionHighlightBackground': '#ff79c6', // Цвет фона для выделений
    'editor.wordHighlightBackground': '#8be9fd', // Цвет для подсветки слов
    'editor.wordHighlightStrongBackground': '#50fa7b', // Цвет для подсветки сильных слов
    'editorBracketMatch.background': '#ffb86c', // Цвет для совпадений скобок
    'editorBracketMatch.border': '#ff79c6', // Цвет границы для совпадений скобок
  }
});
