import React, { useState, useEffect } from 'react';
import * as monaco from 'monaco-editor';

// Компонент для редактора VHDL
const VHDLEditor = ({ onCodeChange }) => {
  useEffect(() => {
    const editor = monaco.editor.create(document.getElementById('vhdl-editor'), {
      value: `-- Напишите ваш VHDL код здесь
architecture Behavioral of testbench is
begin
  -- Ваш тест
end Behavioral;`,
      language: 'vhdl',
      theme: 'vs-dark',
      automaticLayout: true,
    });

    // Отслеживаем изменения в редакторе
    editor.onDidChangeModelContent(() => {
      const code = editor.getValue();
      onCodeChange(code); // Передаем код в родительский компонент
    });

    return () => {
      editor.dispose();
    };
  }, [onCodeChange]);

  return <div id="vhdl-editor"></div>;
};

// Компонент для компиляции и отображения ошибок
const CompilerConsole = ({ output }) => {
  return (
    <div>
      <h3>Консоль:</h3>
      <pre>{output}</pre>
    </div>
  );
};

// Основной компонент для редактирования VHDL и компиляции
const VHDLEditorWithCompiler = () => {
  const [vhdlCode, setVhdlCode] = useState('');
  const [output, setOutput] = useState('');

  // Функция для компиляции кода
  const compileCode = async () => {
    try {
      const response = await fetch('/compile-vhdl', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ vhdlCode }),
      });
      const result = await response.text();
      setOutput(result); // Отображаем вывод компиляции
    } catch (error) {
      setOutput('Ошибка компиляции: ' + error.message); // Выводим ошибку
    }
  };

  // Обновляем код из редактора
  const handleCodeChange = (code) => {
    setVhdlCode(code);
  };

  return (
    <div>
      <h3>VHDL Код:</h3>
      <VHDLEditor onCodeChange={handleCodeChange} />
      
      <div style={{ marginTop: '20px' }}>
        <h3>Тестовый стенд:</h3>
        <textarea 
          value={vhdlCode}
          onChange={(e) => setVhdlCode(e.target.value)} 
          placeholder="Введите код теста..." 
          style={{ width: '100%', height: '200px' }}
        />
      </div>

      <button onClick={compileCode} style={{ marginTop: '20px' }}>Компилировать</button>

      <CompilerConsole output={output} />
    </div>
  );
};

export default VHDLEditorWithCompiler;
