import React, { useState } from 'react';
import GraphComponent, { FileUploader } from './components/GraphComponent'; // Предполагается, что FileUploader не экспортируется по умолчанию

export default function App() {
  const [value, setValue] = useState('') // Используем состояние для хранения значения графа

  // Функция для обновления графа
  const handleChange = (value) => {
    setValue(value); // Сохраняем новое значение графа
  };

  return (
    <>
      {/* Передаем функцию обновления графа и текущее значение в FileUploader */}
      <FileUploader graph2={value} />
      {/* Передаем граф в GraphComponent */}
      <GraphComponent graph={handleChange} />
    </>
  );
}
