import React, { useState } from 'react';
import { renderWaveForm } from 'wavedrom';

const SignalGraph = ({ vcdContent }) => {
  const containerRef = React.useRef(null);

  useEffect(() => {
    if (vcdContent && containerRef.current) {
      // Пример данных. Замените на преобразование VCD в JSON.
      const waveData = {
        signal: [
          { name: 'clk', wave: 'P.....|...' },
          { name: 'data', wave: 'x.345x|=.x', data: ['A', 'B', 'C', 'D'] },
        ],
      };
      renderWaveForm(waveData, containerRef.current);
    }
  }, [vcdContent]);

  return <div ref={containerRef}></div>;
};

export default SignalGraph;
