import * as monaco from 'monaco-editor';

// Определяем язык VHDL
monaco.languages.register({ id: 'vhdl' });

// Определяем подсветку синтаксиса
monaco.languages.setMonarchTokensProvider('vhdl', {
  keywords: [
    'library', 'use', 'entity', 'architecture', 'is', 'port', 'in', 'out',
    'begin', 'end', 'signal', 'process', 'if', 'then', 'else', 'elsif', 
    'case', 'when', 'loop', 'while', 'for', 'generate', 'component', 
    'generic', 'map', 'report', 'severity'
  ],
  operators: [
    '<=', '=>', '=', '/=', '<', '<=', '>', '>=', '+', '-', '*', '/', '&', '|', 'and', 'or', 'not'
  ],
  symbols: /[=><!~?:&|+\-*\/\^%]+/,
  tokenizer: {
    root: [
      // Ключевые слова
      [/\b(?:library|use|entity|architecture|is|port|in|out|begin|end|signal|process|if|then|else|elsif|case|when|loop|while|for|generate|component|generic|map|report|severity)\b/, 'keyword'],
      // Литералы
      [/\b\d+(\.\d+)?([eE][\-+]?\d+)?\b/, 'number'],
      // Комментарии
      [/--.*/, 'comment'],
      // Операторы
      [/[=><!~?:&|+\-*\/\^%]+/, 'operator'],
      // Идентификаторы
      [/\b[a-zA-Z_][a-zA-Z0-9_]*\b/, 'identifier'],
      // Строки
      [/"([^"\\]|\\.)*$/, 'string.invalid'], // Неполная строка
      [/"([^"\\]|\\.)*"/, 'string'],
    ],
  },
});

// Настраиваем отображение
monaco.editor.defineTheme('vhdlTheme', {
  base: 'vs-dark',
  inherit: true,
  rules: [
    { token: 'keyword', foreground: '569cd6' },
    { token: 'number', foreground: 'b5cea8' },
    { token: 'string', foreground: 'ce9178' },
    { token: 'comment', foreground: '6a9955' },
    { token: 'operator', foreground: 'dcdcaa' },
    { token: 'identifier', foreground: 'd4d4d4' },
  ],
});
