import React, { useState, useEffect, useRef } from 'react';
import * as monaco from 'monaco-editor';

const VHDLEditor = ({ id, onCodeChange, initialCode }) => {
  const editorRef = useRef(null);

  useEffect(() => {
    if (editorRef.current) {
      const editorInstance = monaco.editor.create(editorRef.current, {
        value: initialCode,
        language: 'vhdl',
        theme: 'vs-dark',
        automaticLayout: true,
      });

      const debounceChangeHandler = debounce(() => {
        const code = editorInstance.getValue();
        onCodeChange(code);
      }, 300);

      editorInstance.onDidChangeModelContent(debounceChangeHandler);

      return () => {
        editorInstance.dispose();
      };
    }
  }, [initialCode, onCodeChange]);

  return <div id={id} ref={editorRef} className="vhdl-editor"></div>;
};

function debounce(func, wait) {
  let timeout;
  return function () {
    clearTimeout(timeout);
    timeout = setTimeout(func, wait);
  };
}

const CompilerConsole = ({ output }) => (
  <div className="compiler-console">
    <h3>Консоль:</h3>
    <pre>{output}</pre>
  </div>
);

const VHDLEditorWithCompiler = () => {
  const [architectureCode, setArchitectureCode] = useState(`library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity AND_Gate is
    Port (
        A : in STD_LOGIC;
        B : in STD_LOGIC;
        Y : out STD_LOGIC
    );
end AND_Gate;

architecture Behavioral of AND_Gate is
begin
    Y <= A and B;
end Behavioral;`);
  const [testbenchCode, setTestbenchCode] = useState(`library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity AND_Gate_TB is
end AND_Gate_TB;

architecture Behavioral of AND_Gate_TB is
    component AND_Gate
        Port (
            A : in STD_LOGIC;
            B : in STD_LOGIC;
            Y : out STD_LOGIC
        );
    end component;

    signal A : STD_LOGIC := '0';
    signal B : STD_LOGIC := '0';
    signal Y : STD_LOGIC;

begin
    UUT: AND_Gate
        Port map (
            A => A,
            B => B,
            Y => Y
        );

    stimulus: process
    begin
        A <= '0'; B <= '0';
        wait for 10 ns;
        A <= '0'; B <= '1';
        wait for 10 ns;
        A <= '1'; B <= '0';
        wait for 10 ns;
        A <= '1'; B <= '1';
        wait for 10 ns;
        wait;
    end process;
end Behavioral;`);
  const [output, setOutput] = useState('');

  const compileCode = async () => {
    try {
      const response = await fetch('http://localhost:5000/compile-vhdl', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ architectureCode, testbenchCode }),
      });
      const result = await response.text();
      setOutput(result);
    } catch (error) {
      setOutput('Ошибка компиляции: ' + error.message);
    }
  };

  useEffect(() => {
    const compileButton = document.getElementById('compile-html'); // Ищем кнопку в DOM
    if (compileButton) {
      compileButton.onclick = compileCode; // Привязываем обработчик
    }

    return () => {
      if (compileButton) {
        compileButton.onclick = null; // Очищаем привязку при размонтировании
      }
    };
  }, [compileCode]);

  return (
    <div id="content-container" className="content-container">
      <h3>Архитектура:</h3>
      <VHDLEditor
        id="architecture-editor"
        onCodeChange={setArchitectureCode}
        initialCode={architectureCode}
      />

      <h3>Тестовый стенд:</h3>
      <VHDLEditor
        id="testbench-editor"
        onCodeChange={setTestbenchCode}
        initialCode={testbenchCode}
      />

      <CompilerConsole output={output} />
    </div>
  );
};

export default VHDLEditorWithCompiler;
